import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';import '../../osama/widgets/grid_tile_widget.dart';//Screen 43class AdminPage extends StatefulWidget {  @override  _AdminPageState createState() => _AdminPageState();}class _AdminPageState extends State<AdminPage> {  Future<bool> _onWillPop() async {    return (await showDialog(          context: context,          builder: (context) => new AlertDialog(            title: new Text('Are you sure?'),            content: new Text('Do you want to exit an App'),            actions: <Widget>[              new FlatButton(                onPressed: () => Navigator.of(context).pop(false),                child: new Text('No'),              ),              new FlatButton(                onPressed: () => Navigator.of(context).pop(true),                child: new Text('Yes'),              ),            ],          ),        )) ??        false;  }  @override  Widget build(BuildContext context) {    final size = MediaQuery.of(context).size;    final height = size.height -        AppBar().preferredSize.height -        MediaQuery.of(context).padding.top;    return WillPopScope(      onWillPop: _onWillPop,      child: Scaffold(          backgroundColor: Colors.grey[50],          appBar: AppBar(            elevation: 10,            centerTitle: true,            backgroundColor: Colors.white,            title: Image.asset(              'assets/images/logo.png',              width: 150,            ),            actions: <Widget>[              IconButton(                  icon: Icon(Icons.exit_to_app),                  onPressed: () async {                    await FirebaseAuth.instance.signOut();                    Navigator.of(context)                        .pushNamedAndRemoveUntil('/login', (route) => false);                  })            ],          ),          body: ScrollConfiguration(            behavior: MyBehavior(),            child: GridView(              padding: const EdgeInsets.symmetric(horizontal: 30, vertical: 20),              gridDelegate: SliverGridDelegateWithMaxCrossAxisExtent(                  // crossAxisSpacing: 30,                  // mainAxisSpacing: 20,                //    childAspectRatio: 1,                  maxCrossAxisExtent: 200),              //shrinkWrap: true,              children: [                GridTileWidget(                  route: '/finance',                  title: 'Finance',                  icon: 'assets/images/FinanceIcon.png',                ),                GridTileWidget(                  route: '/distributionHome',                  title: 'Distribution',                  icon: 'assets/images/DistributionIcon.png',                ),                GridTileWidget(                  route: '/orderScreen',                  title: 'Orders',                  icon: 'assets/images/OrdersIcon.png',                ),                GridTileWidget(                  route: '',                  title: 'Represent-\natives',                  icon: 'assets/images/SalesIcon.png',                ),                GridTileWidget(                  route: '',                  title: 'Customer\nService',                  icon: 'assets/images/CustomerServiceIcon.png',                ),                GridTileWidget(                  route: '/issueHome',                  title: 'Reports',                  icon: 'assets/images/Report.png',                ),              ],            ),          )),    );  }}